import { useMutation, useSubscription } from '@apollo/client'
import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import {  NEW_VOTE_MUTATION, QUESTION_DETAIL_SUBSCRIPTION } from './queries'
import Loading from '../../components/Loading'

function QuestionDetail() {


    const {id} = useParams()

    const [selectedOptionId, setSelectedOptionId] = useState()


    const {loading,error,data} = useSubscription(QUESTION_DETAIL_SUBSCRIPTION,{
        variables:{
           id
        }
    })

        
    const [newVote, {loading:loadingVote,data:voteData}] = useMutation(NEW_VOTE_MUTATION)


    if (loading) {
        return <Loading />;
      }
    
      if (error) {
        return <div>Error: {error.message}</div>;
      }


      const {questions_by_pk: {options,title}} = data

      const handleClickVote = () => {
        newVote({
            variables:{
                input:{
                   option_id:selectedOptionId
                }
            }
        })
      }

    console.log(data)


  return (
   <>
         <div>Question Detail {id}</div>
         <h1>{title}?</h1>

        <div>
            
        {
                options.map((option,i)=> (
                        <label style={{display:"block"}} htmlFor={i} key={i}>
                        
                        <input style={{width:"50px"}} name='selected' type='radio' value={option.id} onChange={({target}) => setSelectedOptionId(target.value)}/>
                        <span>{option.title}</span>
                        </label>
                ))
            }


            <br/>
            <br/>
            <button disabled={loadingVote} onClick={handleClickVote}>Vote</button>


        </div>



   </>
  )
}

export default QuestionDetail
