import { gql } from "@apollo/client";

export const GET_QUESTION_DETAIL = gql`
query getQuestionDetail($id: Int) {
  options(where: {id: {_eq: $id}}) {
    title
    question {
      title
      options{
        title
      }
    }
  }
}
`
