import { useSubscription } from '@apollo/client'
import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import {  QUESTION_DETAIL_SUBSCRIPTION } from './queries'
import Loading from '../../components/Loading'

function QuestionDetail() {


    const {id} = useParams()

    const [selected, setSelected] = useState()


    const {loading,error,data} = useSubscription(QUESTION_DETAIL_SUBSCRIPTION,{
        variables:{
           id
        }
    })

    if (loading) {
        return <Loading />;
      }
    
      if (error) {
        return <div>Error: {error.message}</div>;
      }


      const {questions_by_pk: {options,title}} = data


    console.log(data)


  return (
   <>
         <div>Question Detail {id}</div>
         <h1>{title}?</h1>

            {
                options.map((option,i)=> (
                        <label htmlFor={i} key={i}>

                        <input name='selected' type='radio' value={option.id} onChange={({target}) => setSelected(target.value)}/>
                        <b>{option.title}</b>
                        </label>
                ))
            }



   </>
  )
}

export default QuestionDetail
